                    var fullHieght = $(document).height() - $(window).height();
                    if (scrolled < fullHieght) {
                        $paginationDiv.animate({"bottom": "0px"});
                    }
                    startedApplyingDelayedAdjustment = false;
                    applyDelayedAdjustment = false;

